import { Fragment, useContext } from "react"
import { Link, Outlet } from "react-router-dom"
import { UserContext } from "../contexts/userContext"
import { signOutUser } from "../utils/firebase"
import './'
const Navigation = () => {
    const { currentUser, setCurrentUser } = useContext(UserContext)
    console.log(currentUser)
    const signOutHandler = async () => {
        await signOutUser();
        setCurrentUser(null);
    }
    return (
        <Fragment>
            
            <div className="nav left">
                <span className="gradient skew"><h1 className="logo un-skew"><Link className="home-container" to='/'>Logo
                </Link></h1></span>
                <button id="menu" className="btn-nav"><span className="fas fa-bars"></span></button>
            </div>
            <div className="nav right">
                <Link className="nav-link active" to='/'><span className="nav-link-span"><span className="u-nav">Home</span></span>
                </Link>
                {!currentUser &&
                    <Link className="nav-link" to='/signup'>


                        <span className="nav-link-span"><span className="u-nav">Signup</span></span>
                    </Link>}
                <Link className="nav-link" to='/about'>

                    <span className="nav-link-span"><span className="u-nav">About</span></span>
                </Link>
                {
                    currentUser ? (<Link className="nav-link" onClick={signOutHandler}><span className="nav-link-span"><span className="u-nav">Signout</span></span></Link>) : (<Link className="nav-link" to='/signin'> <span className="nav-link-span"><span className="u-nav">Signin</span></span> </Link>)


                }

            </div>

            <Outlet />
        </Fragment>
    )
}
export default Navigation
