import {useState} from 'react'
const defaultFormFields = {
    displayName : '',
    email: '',
    password: '',
    confirmPassword: ''
}
const Signup = () => {
    const [formFields, setFormFields] = useState(defaultFormFields)
    const {displayName, email, password, confirmPassword} = defaultFormFields;
    console.log(formFields.displayName)
    const handleChange = (event) => {
        const {name, value} = event.target;
        setFormFields({...formFields, [name]: value})
    }
    return (
        <div>
            <h1>Sign up</h1>
            <br></br>
            <form onSubmit={()=> {}}>
                <label>Name :</label>
                <input type='text' name='displayName' onChange={handleChange} value={displayName} required/>
                <br/>
                <label>Email : </label>
                <input type='email' name='email' value={email} required/>
                <br/>
                <label>Password</label>
                <input type='password' name='pass' value={password} required/>
                <br/>
                <label>Confirm Password</label>
                <input type='password' name='cpass' value={confirmPassword} required/>
                <button type="submit">Sign Up</button>
            </form>

        </div>
    )
}


export default Signup;
