import { useState, useContext } from 'react';


import FormInput from '../form-input/form-input-component';
import Button from '../button/button-component';
import { UserContext } from '../../contexts/userContext';
import './sign-in-form.component/'
import {
    signInWithGooglePopup,
    createUserDocumentFromAuth,
    signInAuthUserWithEmailAndPassword
} from '../../utils/firebase'



const defaultFormFields = {
    email: '',
    password: '',
};

const SignInForm = () => {
    const [formFields, setFormFields] = useState(defaultFormFields);
    const { email, password } = formFields;

    const { setCurrentUser } = useContext(UserContext);


    const resetFormFields = () => {
        setFormFields(defaultFormFields);
    };

    const signInWithGoogle = async () => {
        const { user } = await signInWithGooglePopup();
        await createUserDocumentFromAuth(user);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const { user } = await signInAuthUserWithEmailAndPassword(
                email,
                password
            );
            setCurrentUser(user);
            // console.log(response);
            resetFormFields();
        } catch (error) {
            switch (error.code) {
                case 'auth/wrong-password':
                    alert('incorrect password for email');
                    break;
                case 'auth/user-not-found':
                    alert('no user associated with this email');
                    break;
                default:
                    console.log(error);
            }
        }
    };

    const handleChange = (event) => {
        const { name, value } = event.target;

        setFormFields({ ...formFields, [name]: value });
    };

    return (
        <div className='box-form'>
            <div class="left">
                <div class="overlay">
                    <h1>Hello World.</h1>
                    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                        Curabitur et est sed felis aliquet sollicitudin</p>
                    <span>
                        <p>login with social media</p>
                        <a href="#"><i class="fa fa-facebook" aria-hidden="true"></i></a>
                        <a href="#"><i class="fa fa-twitter" aria-hidden="true"></i> Login with Twitter</a>
                    </span>
                </div>
            </div>
            <div>
                <div className='sign-up-container'>
                    <h2>Already have an account?</h2>
                    <span>Sign in with your email and password</span>
                    <form onSubmit={handleSubmit}>
                        <div className='inputs'>
                            <input type='email' label='Email' required onChange={handleChange} name='email' value={email} />
                            <input type='password' label='Password' required onChange={handleChange} name='password' value={password} />
                        </div>
                        <div class="remember-me--forget-password">

                            <label>
                                <input type="checkbox" name="item" checked />
                                <span class="text-checkbox">Remember me</span>
                            </label>
                            <p>forget password?</p>
                        </div>

                        <br> </br>
                        <button type='submit'>Login</button>
                        <button type='button' onClick={signInWithGoogle}>Google Sign in</button>
                    </form>
                </div>
            </div>
            </div>
            );
};

            export default SignInForm;
