import {Routes, Route} from 'react-router-dom'
import { redirect } from 'react-router-dom'
import Home from './components/home'
//import Navigation from './components/navigation'
import Navigation from './components/navBar/navi'
import About from './components/about'
// import { useCookies } from 'react-cookie'
//import Signin from './components/sign-in-form/sign-in-form.component'
import { useCookies } from 'react-cookie'
import Dashboard from './components/dashboard/dashboard'
import Signin from './components/sign-in-form/signIn'
import Signup from './components/sign-up-form/sign-up-form.component'
function App() {
  const [cookies, setCookie, removeCookie] = useCookies(["user"]);
  return (
      <Routes>
        <Route path='/' element = {<Navigation/>}>
              <Route index element = {<Home />} />
              <Route path='about' element = {<About/>} />
              <Route path='signin' element = {<Signin/>} />
              <protectedRoute path='/dashboard' auth= {cookies.user} component={Dashboard}/>
              <Route path='signup' element = {<Signup/>} />
        </Route>
      </Routes>

  );
}
const protectedRoute = ({auth, component: Component, ...rest})=> {
      return (<Route {...rest} render = {()=>auth? (<Component/>):
    (<redirect to='/signin'/>)
    }
    />)

}
export default App;
